DocFxMerge("./docs/docfx.json", new DocFxMergeSettings
{
    TocMetadata = { "platform" },
    WarningsAsErrors = true
});

using (var process = DocFxServeStart("./docs")) 
{
    // Launch browser or other action based on the site
    process.WaitForExit();
}

using (var process = DocFxServeStart("./docs"))
{
    // Launch browser or other action based on the site
    process.WaitForExit();
}

